%FIG_GLUCOSE_DEPENDENCY Creates HGU/HGP and glycogen plot.
%   Dependency of HGP & HGU from time and glucose and glycogen dependency
%   from glucose and time.

%   Matthias Koenig (matthias.koenig@charite.de)
%   Copyright 2011 Matthias Koenig
%   date:   110226  -   v0.1

close all
clear all

% data folder
out_folder = '/home/mkoenig/Desktop/results/120222_data'

% select if images should be saved
save_images = 1;        

% simulation condition
condition  = 'normal'
%condition  = 'diabetes'
%condition  = 'insulin_restored'
%condition  = 'glucagon_restored'

% load simulation data
in_file = strcat(out_folder, '/glucose_dependency_', condition);
load(in_file);

% plot range
t_offset = 2;           % offset for t (very rapid changes in first minute not shown)
edge_alpha_val = 0;
pcolor_edge_alpha = 0;

% plot boundaries
glc_min = 2;
glc_max = 12;
t_max = 300; 

% indeces of plot boundaries
glc_ext = mpars.glc_ext;
t_span  = mpars.t_span; 
tmp = find(glc_ext>=glc_min); glc_min_ind = tmp(1);
tmp = find(glc_ext<=glc_max); glc_max_ind = tmp(end);
tmp = find(t_span>=t_max); t_max_ind = tmp(1);
t_min_ind = t_offset;
t_min = 0;

x = glc_ext(glc_min_ind:glc_max_ind);
y = t_span(t_offset:t_max_ind)/60;      % time in [h]

ftype = 1   % 1: pcolor;    2: surf
%contour_lines = -1.2:0.2:1.2;
clines_hgp = [-20:2:20];
map_x_min = -20; map_x_max = 20.0;
ccolor_hgp = [0.2, 0.2, 0.2];
ccolor_glycogen = [0 0 0];


%% figure1 - GLUT2 (surf) - HGU and HGP
fig1 = figure('Name','HGP/HGU', 'Color',[1 1 1]);
map_rg = colormap_gwr(map_x_min, map_x_max);
set(fig1, 'Colormap', map_rg);

% panel 1 - HGP and HGU
z = v_full(glc_min_ind:glc_max_ind, t_offset:t_max_ind, 1);

if ftype == 1
    p1 = pcolor(x, y, z'), hold on
    set(p1, 'EdgeAlpha', edge_alpha_val);
    %Additional contours
    [C,h] = contour(x,y,z',clines_hgp, 'k-', 'LineColor', ccolor_hgp); hold off
    text_handle = clabel(C, h, 'LabelSpacing', 350, 'Rotation',90);
    set(text_handle,'Color',ccolor_hgp);
    
    %clabel(C, clines_hgp)
    %title({'HGP and HGU (v_{GLUT2} [µmol/min/kg])'}, 'FontWeight', 'bold', 'FontSize', 14), 
    ylabel('time [h]'),
    xlabel('blood glucose [mM]'),
    axis([glc_min glc_max t_min/60 t_max/60])
elseif ftype == 2
    surf(y, x, z, 'EdgeAlpha', edge_alpha_val), hold on
    %title({'HGP and HGU'}, 'FontWeight', 'bold', 'FontSize', 14), 
    xlabel('time [h]'),
    ylabel('blood glucose [mM]'),
    zlabel('v_{GLUT2} [µmol/min/kg]')
    view([29.5 38]);
    axis([t_min/60, t_max/60, glc_min, glc_max, -2, 4])
end
colorbar('peer', gca);
caxis([map_x_min, map_x_max])
axis square
shading interp

% save the image
if save_images==1
    disp('* fig1 saved')
    fig_file = strcat(in_file, '_fig1.png')
    saveas(fig1, fig_file,'png'); 
end

% find the zeros
%{
fig3 = figure('Name','HGP/HGU', 'Color',[1 1 1]);
plot(x, z')
title({'HGP and HGU (v_{GLUT2} [µmol/min/kg])'}, 'FontWeight', 'bold', 'FontSize', 14), 
xlabel('blood glucose [mM]', 'FontWeight', 'bold', 'FontSize', 11),
axis square
grid on
%}



